mapper.update:
UPDATE lot
SET status = #{status}
WHERE lot_id = #{lotId}

mapper.select:
SELECT lot_id, bin_id
FROM lot
WHERE product_id = #{productId}
  AND outbound_id IS NULL
ORDER BY created_at ASC
    LIMIT #{requiredLotCount}

SELECT lot_id, bin_id
FROM lot
WHERE product_id = #{productId}
ORDER BY created_at ASC
    LIMIT #{requiredLotCount}
