mapper.select:
SELECT b.outbound_id, b.outbound_plan_id, b.process, b.outbound_schedule_number, b.outbound_assign_number, b.outbound_picking_number, b.outbound_packing_number, b.outbound_packing_date, b.production_plan_number, b.plan_date, -- Product 정보 컬럼 p.product_id, p.product_code, p.product_name, opp.required_quantity AS productCount FROM ( SELECT o.outbound_id, op.outbound_plan_id, op.status AS process, op.outbound_schedule_number, o.outbound_assign_number, o.outbound_picking_number, o.outbound_packing_number, o.outbound_packing_date, op.production_plan_number, op.plan_date FROM outbound o JOIN outbound_plan op ON o.outbound_plan_id = op.outbound_plan_id
outbound_assign_number IS NOT NULL AND outbound_picking_number IS NOT NULL AND outbound_packing_number IS NOT NULL AND outbound_loading_number IS NULL
AND outbound_packing_date >= #{startDate}
AND outbound_packing_date <= #{endDate}
AND outbound_packing_number LIKE CONCAT('%', #{outboundPackingNumber}, '%')
${order.property}
ASC DESC
LIMIT #{pageable.pageSize} OFFSET #{pageable.offset} ) b LEFT JOIN outbound_plan_product opp ON b.outbound_plan_id = opp.outbound_plan_id LEFT JOIN product p ON opp.product_id = p.product_id

SELECT COUNT(*) FROM outbound
outbound_assign_number IS NOT NULL AND outbound_picking_number IS NOT NULL AND outbound_packing_number IS NOT NULL AND outbound_loading_number IS NULL
AND outbound_packing_date >= #{startDate}
AND outbound_packing_date <= #{endDate}
AND outbound_packing_number LIKE CONCAT('%', #{outboundPackingNumber}, '%')

mapper.update:
UPDATE outbound
SET outbound_packing_number = #{outboundPackingNumber},
    outbound_packing_date = #{outboundPackingDate}
WHERE outbound_id = #{outboundId}

UPDATE outbound
SET outbound_packing_date = #{outboundPackingDate}
WHERE outbound_id = #{outboundId}

UPDATE outbound_plan
SET status = #{status}
WHERE outbound_plan_id = #{outboundPlanId}
